<script>import { twMerge } from 'tailwind-merge';
export let icon = false;
export let liClass = '';
let classLi = twMerge(liClass, icon && 'flex items-center', $$props.class);
</script>

<li {...$$restProps} class={classLi}>
  <slot />
</li>

